import { Link } from "react-router-dom";
import '../../css/inscription.css';
import { useLanguage } from '../../store/languageContext';

function Inscription() {
    const { languageData } = useLanguage();

    // Fonction pour vérifier les identifiants via le backend PHP
    const submitInscription = async (login, mdp) => {
        try {
            const response = await fetch('http://localhost/word-box/server/routeur.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                },
                body: new URLSearchParams({
                    objet: 'Client',
                    action: 'inscrire',
                    login: login,
                    mdp: mdp,
                }),
            });

            const data = await response.json();
            console.log('reponse webservice' ,data);
            return data.success;
        } catch (error) {
            console.error("Erreur lors de la vérification des identifiants :", error);
            return false;
        }
    };

    // Appeler la fonction checkCredentials dans handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault();

        const isValid = await submitInscription(login, mdp);
        if (isValid) {
            window.location.href = "/home"; // Rediriger vers la page d'accueil
        } else {
            setErrorMessage("Échec de la connexion. Veuillez vérifier vos identifiants.");
        }
    };
    return (
        <main>

            <div class="wrapper">
                <form onSubmit={handleSubmit}>
                    <input type="hidden" name="objet" value="Traitement_inscription" />
                    <input type="hidden" name="action" value="inscrire" />
                    <h1 data-translate-key="signIn">{languageData.signIn}</h1>
                    <div class="input-box">
                        <input type="text" name="nomClient" placeholder={languageData.lastName} required />
                        <i class='bx bxs-user'></i>
                    </div>
                    <div class="input-box">
                        <input type="text" name="prenomClient" placeholder={languageData.firstName} required />
                        <i class='bx bxs-user'></i>
                    </div>
                    <div class="input-box">
                        <input type="text" name="login" placeholder={languageData.username} required />
                        <i class='bx bxs-user-circle'></i>
                    </div>
                    <div class="input-box">
                        <input type="password" name="mdp" placeholder={languageData.password} required />
                        <i class='bx bxs-lock-alt'></i>
                    </div>
                   

                    <div class="souvenir-oublie">
                        <Link to="/connexion" data-translate-key="connect" >{languageData.connect}</Link>
                    </div>
                    <button type="submit" class="btn link-color" data-translate-key="register" >{languageData.register}</button>
                </form>
            </div>
        </main>
    )
}

export default Inscription;
