import React, { createContext, useState, useEffect, useContext } from 'react';
import { translations, changeLanguage } from './script/translate'; // Import de la fonction et des traductions

// Création du contexte pour la langue
const LanguageContext = createContext();

// Provider pour gérer la langue et les traductions
const LanguageProvider = ({ children }) => {
    const [language, setLanguage] = useState(localStorage.getItem('lang') || 'en'); // La langue par défaut est 'en'
    const [languageData, setLanguageData] = useState(translations[language]); // Récupère directement les données de traduction

    // Effet pour appliquer la langue au montage et au changement de langue
    useEffect(() => {
        // Mettre à jour la langue dans le localStorage
        localStorage.setItem('lang', language);

        // Appliquer les traductions sur le DOM
        changeLanguage(language); // Mettre à jour la langue sur les éléments DOM qui ont des clés de traduction
        setLanguageData(translations[language]); // Mettre à jour les données de langue
    }, [language]); // Ré-exécuter lorsque `language` change

    return (
        <LanguageContext.Provider value={{ language, setLanguage, languageData }}>
            {children}
        </LanguageContext.Provider>
    );
};

// Hook pour accéder au contexte de la langue
export const useLanguage = () => useContext(LanguageContext);

export { LanguageContext, LanguageProvider };
