import React, { createContext, useState, useContext } from 'react';
import { classByTheme } from '../script/change-theme'; // Assure-toi que cette fonction change le texte du DOM

const ThemeContext = createContext();

const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState(localStorage.getItem('theme') || 'en');
    const [themeData, setThemeData] = useState(classByTheme[theme]); // Récupère les données de traduction

    const toggleTheme = () => {
        setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
    };

    useEffect(() => {
        localStorage.setItem('theme', theme);
         setThemeData(classByTheme[theme]);
    }, [theme]);

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme, themeData }}>
            {children}
        </ThemeContext.Provider>
    );
};

export const useTheme = () => useContext(ThemeContext);
export { ThemeContext, ThemeProvider };
