import React, { useEffect, useState } from 'react';

const ListeMot = () => {
    // Liste des mots
    const mots = [
        { mot: "Chicken" },
        { mot: "Glaucous" },
        { mot: "Pip squeak" },
        { mot: "Inspiration" },
        { mot: "Cockerel" }
    ];

    // État pour stocker les définitions obtenues
    const [definitions, setDefinitions] = useState({});
    const [translatedMots, setTranslatedMots] = useState({});
    let langCurrent = localStorage.getItem("lang") || "fr";

    const translateText = async (text, targetLang) => {
        const LIBRETRANSLATE_URL = 'https://libretranslate.com/translate';

        try {
            const response = await fetch(LIBRETRANSLATE_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    q: text,
                    source: 'en',
                    target: targetLang,
                    format: 'text',
                }),
            });
    
            // Vérifiez si la réponse est OK
            if (!response.ok) {
                console.error(`Erreur HTTP: ${response.status}`);
                return text;
            }
    
            const data = await response.json();
            console.log('Réponse LibreTranslate:', data); // Log de la réponse complète
    
            if (data.error) {
                console.error('Erreur dans la réponse:', data.error);
                return text;
            }
    
            return data.translatedText || text;
        } catch (error) {
            console.error('Erreur lors de la requête LibreTranslate:', error);
            return text;
        }
    };
    

    // Fonction pour récupérer la définition de chaque mot depuis l'API DictionaryAPI.dev
    const fetchDefinitionFromDictionaryAPI = async (mot) => {
        const url = `https://api.dictionaryapi.dev/api/v2/entries/en/${mot}`;
        try {
            const response = await fetch(url);
            const data = await response.json();
            const definition = data[0]?.meanings[0]?.definitions[0]?.definition || "Définition introuvable";
            setDefinitions((prevDefinitions) => ({
                ...prevDefinitions,
                [mot]: definition
            }));
        } catch (error) {
            console.error(`Erreur lors du chargement de la définition pour ${mot} depuis DictionaryAPI.dev:`, error);
            setDefinitions((prevDefinitions) => ({
                ...prevDefinitions,
                [mot]: "Erreur de chargement"
            }));
        }
    };

    // Fonction pour traduire les mots en fonction de la langue choisie
    const fetchTranslations = async () => {
        if (langCurrent === 'en') {
            setTranslatedMots(mots.reduce((acc, { mot }) => ({ ...acc, [mot]: mot }), {}));
        } else {
            const translated = {};
            for (const { mot } of mots) {
                translated[mot] = await translateText(mot, langCurrent);
            }
            setTranslatedMots(translated);
        }
    };
    

    // Récupérer les définitions et traductions au chargement du composant
    useEffect(() => {
        const fetchData = async () => {
            try {
                mots.forEach(({ mot }) => {
                    fetchDefinitionFromDictionaryAPI(mot);
                });
                await fetchTranslations();
            } catch (error) {
                console.error('Erreur lors du chargement des données:', error);
            }
        };
    
        fetchData();
    }, [langCurrent]);
    

    const supprFavoris = (item) => {
        console.log("coeur cliqué !");
        document.getElementById(item.mot).className =
            document.getElementById(item.mot).className === "bi bi-suit-heart-fill"
                ? "bi bi-suit-heart"
                : "bi bi-suit-heart-fill";
    };

    return (
        <div>
            {mots.map((item, index) => (
                <div key={index} className="wrapper">
                    <h1>{translatedMots[item.mot] || item.mot}</h1>
                    <i
                        className="bi bi-suit-heart-fill"
                        id={item.mot}
                        onClick={() => supprFavoris(item)}
                    ></i>
                    <p>{definitions[item.mot] || "Chargement..."}</p>
                </div>
            ))}
        </div>
    );
};

export default ListeMot;
