// src/App.js
import React from "react";
import { useState } from "react";
import $ from "jquery";
import '../../css/accueil.css';
import { useLanguage } from '../../store/languageContext';

function Home() {
    const { language, setLanguage, languageData } = useLanguage();

    const [word, setWord] = useState("");
    const [result, setResult] = useState("");

   

    const handleSubmit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        fetch(form.attr("action"), {
            method: "POST",
            headers: {
            "Content-Type": "application/x-www-form-urlencoded",
            },
            body: new URLSearchParams(new FormData(form[0])).toString(),
        })
        .then(response => response.text())
        .then(data => {
            setResult(data);
        })
        .catch(error => {
            console.error('Error:', error);
        });
    };
    return (
        <div class="container">
            <div class="wrapper fit-box center-text">
                <h2 data-translate-key="titleSearch">{languageData.titleSearch}</h2>
                <form
                    onSubmit={(event) => handleSubmit(event)}
                >
                    <div class="input-box "><input
                        type="text"
                        id="search"
                        name="search"
                        value={word}
                        onChange={(event) =>
                            setWord(event)
                        }
                    /></div>
                    <button class="btn" type="submit" data-translate-key="search">{languageData.search}</button>

                </form>

                <div class="result">

                    
                </div>
            </div>
        </div>
    );
}

export default Home;

