import React, { useEffect, useState } from 'react';

const ListeMot = () => {
  // Liste des mots
  const mots = [
    { mot: 'Chicken' },
    { mot: 'Glaucous' },
    { mot: 'Pip squeak' },
    { mot: 'Inspiration' },
    { mot: 'Cockerel' },
  ];

  // États pour stocker les définitions et traductions
  const [definitions, setDefinitions] = useState({});
  const [translatedMots, setTranslatedMots] = useState({});
  const [langCurrent, setLangCurrent] = useState(localStorage.getItem('lang') || 'fr');

  // Fonction pour récupérer la définition via l'API DictionaryAPI
  const fetchDefinitionFromDictionaryAPI = async (mot) => {
    const url = `https://api.dictionaryapi.dev/api/v2/entries/en/${mot}`;
    try {
      const response = await fetch(url);
      const data = await response.json();
      const definition = data[0]?.meanings[0]?.definitions[0]?.definition || 'Définition introuvable';
      setDefinitions((prevDefinitions) => ({
        ...prevDefinitions,
        [mot]: definition,
      }));
    } catch (error) {
      console.error(`Erreur lors du chargement de la définition pour ${mot}:`, error);
      setDefinitions((prevDefinitions) => ({
        ...prevDefinitions,
        [mot]: 'Erreur de chargement',
      }));
    }
  };

  // Fonction pour traduire le texte via l'API Google Translate gratuite
  const translateText = async (text, targetLang) => {
    const sourceLang = 'en';
    const url = `https://translate.googleapis.com/translate_a/single?client=gtx&sl=${sourceLang}&tl=${targetLang}&dt=t&q=${encodeURI(text)}`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      return data?.[0]?.[0]?.[0] || text;
    } catch (error) {
      console.error('Erreur lors de la traduction:', error);
      return text;
    }
  };

  // Fonction pour traduire tous les mots en fonction de la langue choisie
  const fetchTranslations = async () => {
    if (langCurrent === 'en') {
      setTranslatedMots(mots.reduce((acc, { mot }) => ({ ...acc, [mot]: mot }), {}));

    } else {
      const translated = {};

      await Promise.all(
        mots.map(async ({ mot }) => {
          translated[mot] = await translateText(mot, langCurrent);
        })
      );
      setTranslatedMots(translated);
    }
    const translatedDef = {};

    await Promise.all(
        definitions.map(async ({ def }) => {
            translatedDef[def] = await translateText(def, langCurrent);
        })
      );
      setDefinitions(translated);
  };

  // Charger les définitions et traductions lors du chargement initial
  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all(mots.map(({ mot }) => fetchDefinitionFromDictionaryAPI(mot)));
        await fetchTranslations();
      } catch (error) {
        console.error('Erreur lors du chargement des données:', error);
      }
    };
    fetchData();
  }, [langCurrent]);

  // Surveiller les changements dans `localStorage` pour la langue
  useEffect(() => {
    const handleStorageChange = () => {
      const newLang = localStorage.getItem('lang') || 'fr';
      setLangCurrent(newLang);
      console.log('Langue actuelle:', newLang);
    };

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  // Charger Google Translate Widget
  useEffect(() => {
    const loadGoogleTranslate = () => {
      if (!window.google) {
        const script = document.createElement('script');
        script.src = "https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit";
        script.async = true;
        script.onerror = () => console.error("Erreur lors du chargement du script Google Translate");
        document.body.appendChild(script);
      } else {
        initializeGoogleTranslate();
      }
    };
  
    const initializeGoogleTranslate = () => {
      // Assurez-vous que la fonction est définie
      if (typeof window.googleTranslateElementInit === "function") {
        new window.google.translate.TranslateElement({
          pageLanguage: langCurrent,
        }, "google_translate_element");
      } else {
        console.error('La fonction googleTranslateElementInit n\'est pas définie');
      }
    };
  
    if (window.google && window.google.translate) {
      initializeGoogleTranslate();
    } else {
      loadGoogleTranslate();
    }
  }, [langCurrent]);
  

  return (
    <div>
      <div id="google_translate_element"></div>
      {mots.map((item, index) => (
        <div key={index} className="wrapper">
          <h1>{translatedMots[item.mot] || item.mot}</h1>
          <p><strong>Définition:</strong> {definitions[item.mot] || 'Chargement...'}</p>
        </div>
      ))}
    </div>
  );
};

export default ListeMot;
