import React, { useEffect, useState } from 'react';

const ListeMot = () => {
    const mots = [
        { mot: "Chicken" },
        { mot: "Glaucous" },
        { mot: "Pip squeak" },
        { mot: "Inspiration" },
        { mot: "Cockerel" }
    ];

    const [definitions, setDefinitions] = useState({});
    const [translatedMots, setTranslatedMots] = useState({});
    const [langCurrent, setLangCurrent] = useState(localStorage.getItem("lang") || "fr");

    console.log('Langue actuelle au début :', langCurrent);

    const translateText = async (text, targetLang) => {
        const LIBRETRANSLATE_URL = 'https://libretranslate.com/translate';

        try {
            const response = await fetch(LIBRETRANSLATE_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    q: text,
                    source: 'en',
                    target: targetLang,
                    format: 'text',
                }),
            });

            if (!response.ok) {
                console.error(`Erreur HTTP: ${response.statusText}`);
                return text;
            }

            const data = await response.json();
            return data.translatedText || text;
        } catch (error) {
            console.error('Erreur lors de la requête LibreTranslate:', error);
            return text;
        }
    };

    const fetchDefinitionFromDictionaryAPI = async (mot) => {
        const url = `https://api.dictionaryapi.dev/api/v2/entries/en/${mot}`;
        try {
            const response = await fetch(url);
            const data = await response.json();
            const definition = data[0]?.meanings[0]?.definitions[0]?.definition || "Définition introuvable";
            setDefinitions((prevDefinitions) => ({
                ...prevDefinitions,
                [mot]: definition
            }));
        } catch (error) {
            console.error(`Erreur lors du chargement de la définition pour ${mot} depuis DictionaryAPI.dev:`, error);
            setDefinitions((prevDefinitions) => ({
                ...prevDefinitions,
                [mot]: "Erreur de chargement"
            }));
        }
    };

    const fetchTranslations = async () => {
        if (langCurrent === 'en') {
            setTranslatedMots(mots.reduce((acc, { mot }) => ({ ...acc, [mot]: mot }), {}));
        } else {
            const translated = {};
            await Promise.all(
                mots.map(async ({ mot }) => {
                    translated[mot] = await translateText(mot, langCurrent);
                })
            );
            setTranslatedMots(translated);
        }
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                await Promise.all(mots.map(({ mot }) => fetchDefinitionFromDictionaryAPI(mot)));
                await fetchTranslations();
            } catch (error) {
                console.error('Erreur lors du chargement des données:', error);
            }
        };
        fetchData();
    }, [langCurrent]);

    useEffect(() => {
        const handleStorageChange = () => {
            const newLang = localStorage.getItem("lang") || "fr";
            setLangCurrent(newLang);
        };

        window.addEventListener('storage', handleStorageChange);
        return () => {
            window.removeEventListener('storage', handleStorageChange);
        };
    }, []);

    // Charger Google Translate
    useEffect(() => {
        const loadGoogleTranslate = () => {
            if (!window.google) {
                const script = document.createElement('script');
                script.src = "//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit";
                script.async = true;
                document.body.appendChild(script);
            }
        };

        window.googleTranslateElementInit = () => {
            if (window.google) {
                new window.google.translate.TranslateElement(
                    { pageLanguage: langCurrent },
                    'google_translate_element'
                );
            }
        };

        loadGoogleTranslate();
    }, [langCurrent]);

    return (
        <div>
            <div id="google_translate_element"></div>
            {mots.map((item, index) => (
                <div key={index} className="wrapper">
                    <h1>{translatedMots[item.mot] || item.mot}</h1>
                    <i
                        className="bi bi-suit-heart-fill"
                        id={item.mot}
                        onClick={() => console.log("coeur cliqué !")}
                    ></i>
                    <p>{definitions[item.mot] || "Chargement..."}</p>
                </div>
            ))}
        </div>
    );
};

export default ListeMot;
