import React, { useState } from "react";
import { Link } from "react-router-dom";
import '../../css/inscription.css';

function Connexion() {
    // Déclarez les états pour les champs du formulaire et les messages d'erreur
    const [login, setLogin] = useState("");
    const [mdp, setMdp] = useState("");
    const [errorMessage, setErrorMessage] = useState(""); // Pour afficher les messages d'erreur

    const handleSubmit = async (e) => {
        e.preventDefault(); // Empêcher le rechargement de la page

        // Envoyer les données du formulaire au backend PHP via fetch
        try {
            const response = await fetch('http://votre-domaine/chemin-vers-votre-backend.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded', // Type de contenu
                },
                body: new URLSearchParams({
                    objet: 'Client',
                    action: 'connect',
                    login: login,
                    mdp: mdp,
                }),
            });

            const data = await response.json();

            if (data.success) {
                // Si la connexion réussie, rediriger ou afficher un message de succès
                window.location.href = "/dashboard"; // Remplacez "/dashboard" par la page souhaitée après connexion
            } else {
                setErrorMessage(data.message || "Échec de la connexion. Veuillez vérifier vos identifiants.");
            }
        } catch (error) {
            console.error("Erreur lors de la soumission du formulaire :", error);
            setErrorMessage("Une erreur est survenue. Veuillez réessayer.");
        }
    };

    return (
        <main>
            <div className="wrapper">
                <form onSubmit={handleSubmit}>
                    <h1 data-translate-key="connexion">Connexion</h1>
                    <input type="hidden" name="objet" value="objet" />
                    <input type="hidden" name="action" value="connect" />

                    <div className="input-box">
                        <input
                            type="text"
                            name="login"
                            data-translate-key="username"
                            placeholder="Nom d'utilisateur"
                            required
                            value={login}
                            onChange={(e) => setLogin(e.target.value)} // Met à jour l'état
                        />
                        <i className='bx bxs-user'></i>
                    </div>

                    <div className="input-box">
                        <input
                            type="password"
                            name="mdp"
                            data-translate-key="password"
                            placeholder="Mot de passe"
                            required
                            value={mdp}
                            onChange={(e) => setMdp(e.target.value)} // Met à jour l'état
                        />
                        <i className='bx bxs-lock-alt'></i>
                    </div>

                    {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Affiche les erreurs */}

                    <button type="submit" className="btn" data-translate-key="connect">Se connecter </button>
                </form>

                <div className="souvenir-oublie">
                    <input type="checkbox" />
                    <label data-translate-key="rememberMe">Se souvenir de moi</label>

                    <Link to="/mdp-oublie" data-translate-key="forgotPassword">Mot de passe oublié?</Link>
                </div>

                <div className="lien-inscription">
                    <div className="align-content">
                        <p data-translate-key="createAccount">Créer un compte? </p>
                        <Link to="/inscription" data-translate-key="register"> S'inscrire</Link>
                    </div>
                </div>
            </div>
        </main>
    );
}

export default Connexion;
