import React, { useEffect, useState } from 'react';
import { useLanguage } from '../../store/languageContext';

// Définition du composant ListeMot
const ListeMot = () => {
  const mots = [
    { mot: 'Chicken' },
    { mot: 'Glaucous' },
    { mot: 'Pip squeak' },
    { mot: 'Inspiration' },
    { mot: 'Cockerel' },
  ];

  const [definitions, setDefinitions] = useState({});
  const [translatedMots, setTranslatedMots] = useState({});
  const { language, languageData } = useLanguage();



  const supprFavoris = ((item) => {
    console.log("coeur cliqué !");
    document.getElementById(item.mot).className = document.getElementById(item.mot).className === "bi bi-suit-heart-fill" ? "bi bi-suit-heart" : "bi bi-suit-heart-fill";
});

  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all(mots.map(({ mot }) => fetchDefinitionFromDictionaryAPI(mot)));
        await fetchTranslations();
      } catch (error) {
        console.error('Erreur lors du chargement des données:', error);
      }
    };
    fetchData();
  }, [language]);

  useEffect(() => {
    const loadGoogleTranslate = () => {
      if (!window.google) {
        const script = document.createElement('script');
        script.src = "https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit";
        script.async = true;
        script.onerror = () => console.error("Erreur lors du chargement du script Google Translate");
        document.body.appendChild(script);
      } else {
        initializeGoogleTranslate();
      }
    };

    const initializeGoogleTranslate = () => {
      if (typeof window.googleTranslateElementInit === "function") {
        new window.google.translate.TranslateElement({
          pageLanguage: language,
        }, "google_translate_element");
      } else {
        console.error('La fonction googleTranslateElementInit n\'est pas définie');
      }
    };

    if (window.google && window.google.translate) {
      initializeGoogleTranslate();
    } else {
      loadGoogleTranslate();
    }
  }, [language]);

  return (
    <div>
      <div id="google_translate_element"></div>
      {mots.map((item, index) => (
        <div key={index} className="wrapper">
          <h1>{translatedMots[item.mot] || item.mot}</h1>
          <i className="bi bi-suit-heart-fill" id={item.mot} onClick={() => supprFavoris(item)}></i>
          <p><strong>{languageData.definition}</strong> {definitions[item.mot] || 'Chargement...'}</p>
        </div>
      ))}
    </div>
  );
};

export default ListeMot;
