import React from 'react';
import { useLanguage } from '../../store/languageContext';

const AboutUs = () => {
    const { languageData } = useLanguage();
    return (
        <div className="wrapper">
            <h1 data-translate-key="aboutUs">{languageData.aboutUs}</h1>
            <br/>
            <p data-translate-key="txt1Presentation">
            {languageData.txt1Presentation}
            </p>
            <br/>

            <h2 data-translate-key="tiltle2Presentation">Nos missions ?</h2>
            <br/>

            <p data-translate-key="txtMission">
            Notre équipe est composée de passionnés des langues. Nous aimons les mots et nous voulons vous aider à les aimer aussi. Nous sommes une équipe jeune et dynamique qui cherche à vous aider à trouver des mots qui vous font plaisir.
            </p>
            <br/>

            <h2 data-translate-key="tilteTeam">Notre équipe</h2>
            <br/>

            <p data-translate-key="txtTeam">
            Notre équipe est composée de passionnés des langues. Nous aimons les mots et nous voulons vous aider à les aimer aussi. Nous sommes une équipe jeune et dynamique qui cherche à vous aider à trouver des mots qui vous font plaisir.
            </p>
            <br/>

            <h2 data-translate-key="titleContact">Nous contacter</h2>
            <br/>

            <p data-translate-key="txtContact">
            Vous pouvez nous contacter par téléphone ou par email. Nous sommes disponibles pour répondre à vos questions et vous aider à trouver des mots qui vous font plaisir. N'hésitez pas à nous contacter si vous avez des questions ou des suggestions. Nous sommes disponibles via l'adresse contact@wordbox.com.            </p>
        </div>
    );
};

export default AboutUs;
