import { Link } from 'react-router-dom'
import React from 'react';
import '../css/footer.css';
import { useLanguage } from '../store/languageContext';

function Footer() {
    const { language, setLanguage, languageData } = useLanguage();

    return (
        <footer>
            <h2  data-translate-key="credits">&copy; 2024 Boîte à mots. Tous droits réservés.</h2>
            <nav>
                <Link to="/about-us"><p  data-translate-key="aboutUs">About Us</p></Link>
                <div class="contact">
                    <p data-translate-key="phone">Numéro de téléphone : 06 92 69 26 02</p>
                    <p data-translate-key="owner">Propriétaire : Rey Crusty</p>
                    <p data-translate-key="address">Adresse : 1 Crusty Caliente Pizza Del Pollo, Barbès 75018</p>
                </div>
            </nav>
        </footer>
    );
}

export default Footer;
