// store/languageContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { translations, changeLanguage } from '../script/translate'; // Assure-toi que cette fonction change le texte du DOM

// Création du contexte pour la langue
const LanguageContext = createContext();

// Provider pour gérer la langue et les traductions
const LanguageProvider = ({ children }) => {
    const [language, setLanguage] = useState(localStorage.getItem('lang') || 'en'); // Par défaut, la langue est 'en'
    const [languageData, setLanguageData] = useState(translations[language]); // Récupère les données de traduction dès le début

    // Effet pour appliquer la langue au montage et au changement de langue
    useEffect(() => {
        // Mettre à jour la langue dans le localStorage
        localStorage.setItem('lang', language);

        // Appliquer les traductions sur le DOM
        changeLanguage(language); // Mettre à jour les éléments DOM qui ont des clés de traduction

        // Mettre à jour les données de langue dans l'état
        setLanguageData(translations[language]);
    }, [language]); // Exécuter chaque fois que la langue change

    return (
        <LanguageContext.Provider value={{ language, setLanguage, languageData }}>
            {children}
        </LanguageContext.Provider>
    );
};

// Hook personnalisé pour accéder au contexte de la langue dans d'autres composants
export const useLanguage = () => useContext(LanguageContext);

export { LanguageContext, LanguageProvider };
