import { Link } from 'react-router-dom'
import '../css/header.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import { changeLanguage } from '../script/translate.js';
import { useLanguage } from '../store/languageContext';

function Header() {
    const { language, setLanguage, languageData } = useLanguage();
    let langCurrent = localStorage.getItem("lang") || "fr";
    const changeTheme = (() => {
        document.getElementById("theme").className = document.getElementById("theme").className === "bi bi-brightness-high" ? "bi bi-moon-stars-fill" : "bi bi-brightness-high";
    });

    const changeLang = (() => { 
        document.getElementById("lang").className = document.getElementById("lang").className === "flag-icon-fr" ? "flag-icon-en" : "flag-icon-fr";
        let lang = langCurrent === "fr" ? "en" : "fr"; setLanguage(lang);});

        
    return (
        <header class="header">
            <div class="Title"><i class="bi bi-book"></i><h1 style={{ fontFamily: ' Lucida Handwriting' }} data-translate-key="wordBox" >Boîte à mot</h1></div>
            { /* <img src="../img/logo.jpg" alt="logo" /> */}
            <nav class="navbar">
                <div class="align-redirect-page" >
                    <li>
                        <Link to={`/home`} data-translate-key="home" >languageData.home</Link>
                    </li>
                    <li>
                        <Link to={`/connexion`} data-translate-key="connexion">languageData.connexion</Link>
                    </li>
                    <li>
                        <Link to={`/inscription`} data-translate-key="signIn">languageData</Link>
                    </li>
                    <li>
                        <Link to={`/mes-mots`} data-translate-key="favList">Mes mots</Link>
                    </li>
                </div>
                <div className="settings">
                    <i class="bi bi-brightness-high" id="theme" onClick={() => changeTheme()}> </i>
                    <i class="flag-icon-en" id="lang" onClick={() => changeLang()}></i>
                </div>
            </nav>
        </header>
    );
}

export default Header;
