import { Link } from 'react-router-dom'
import '../css/header.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import { changeLanguage } from '../script/translate.js';

function Header() {
    
    let langCurrent = localStorage.getItem("lang") || "fr";
    const changeTheme = (() => {
        document.getElementById("theme").className = document.getElementById("theme").className === "bi bi-brightness-high" ? "bi bi-moon-stars-fill" : "bi bi-brightness-high";
    });

    const changeLang = (() => { let lang = langCurrent === "fr" ? "en" : "fr";  changeLanguage(lang); langCurrent = lang; localStorage.setItem("lang", lang); });

    return (
        <header class="header">
            <div class="Title"><i class="bi bi-book"></i><h1 style={{ fontFamily: ' Lucida Handwriting' }} >Boîte à mot</h1></div>
             <img src="../img/logo.jpg" alt="logo" />
            <nav class="navbar">
                <div class="align-redirect-page" >
                    <li>
                        <Link to={`/home`} data-translate-key="home" >Accueil</Link>
                    </li>
                    <li>
                        <Link to={`/connexion`} data-translate-key="connexion">Connexion</Link>
                    </li>
                    <li>
                        <Link to={`/inscription`} data-translate-key="signIn">Inscription</Link>
                    </li>
                    <li>
                        <Link to={`/mes-mots`} data-translate-key="favList">Mes mots</Link>
                    </li>
                </div>
                <div className="settings">
                    <i class="bi bi-brightness-high" id="theme" onClick={() => changeTheme()}> </i>
                    <i class="bi bi bi-translate" onClick={() => changeLang() }></i>
                </div>
            </nav>
        </header>
    );
}

export default Header;
