import { Link } from "react-router-dom";
import { useLanguage } from '../../store/languageContext';

function MdpOublie() {
    const { languageData } = useLanguage();

    // Appeler la fonction checkCredentials dans handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault();

        const isValid = await submitInscription();
        if (isValid) {
            window.location.href = "/home"; // Rediriger vers la page d'accueil
        } else {
            setErrorMessage("Échec de la connexion. Inscription Failed.");
        }
    };
    return (
        <main>
            <div class="wrapper">
                <form >
                    <h2>{languageData.forgotPassword}</h2>
                    <div class="input-box">
                        <input type="password" placeholder={languageData.newMdp} required />
                        <i class='bx bxs-lock-alt'></i>
                    </div>
                    <div class="input-box">
                        <input type="password" placeholder={languageData.confirmMdp} required />
                        <i class='bx bxs-lock-alt'></i>
                    </div>
                    <button type="submit" class="btn"><Link to="/connexion">OK</Link></button>
                </form>
            </div>
        </main>
    )
}

export default MdpOublie;
